        } else if (result.type === 'commandError') {
            console.log(`âŒ Command failed`);
            console.log(`Error: ${result.error}`);
            console.log(`Exit code: ${result.exitCode}`);
        }

        return result;
    } catch (error) {
        console.error(`â° ${error.message}`);
        throw error;
    }
};

