        skipped: 0,
        total: 0
    };

    testEmitter.on('commandOutput', (data) => {
        const output = data.output;
        console.log('ðŸ§ª Test output:', output);

        // Track test results
        if (output.includes('âœ“') || output.includes('PASS')) {
            testStats.passed++;
            console.log(`âœ… Tests passed: ${testStats.passed}`);
        } else if (output.includes('âœ—') || output.includes('FAIL')) {
            testStats.failed++;
            console.log(`âŒ Tests failed: ${testStats.failed}`);
        } else if (output.includes('SKIP')) {
            testStats.skipped++;
            console.log(`â­ï¸ Tests skipped: ${testStats.skipped}`);
        }

        // Extract test suite information
        if (output.includes('Test Suites:')) {
            console.log('ðŸ“Š Test suite summary detected');
        }

        // Monitor test progress
        const progressMatch = output.match(/(\d+)\/(\d+)/);
        if (progressMatch) {
            const current = parseInt(progressMatch[1]);
            const total = parseInt(progressMatch[2]);
            testStats.total = total;
            console.log(`ðŸ“Š Test progress: ${current}/${total}`);
        }
    });

    testEmitter.on('commandError', (error) => {
        console.error('âŒ Test error:', error.error);

