    if (status.isLocked) {
      console.log(`Locked by ${status.lock.lockedByName}`);
    }
    return;
  }

  try {
    // Do work on the job
    await doWork(jobId);
  } finally {
    await codebolt.job.unlockJob(jobId, agentId);
  }
}
