  console.log(`Progress: ${current}/${total} (${Math.round(current/total*100)}%)`);
});

// Example 8: Add task with retry mechanism
const addTaskWithRetry = async (taskDescription, maxRetries = 3) => {
  let lastError;

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      console.log(`ðŸ”„ Attempt ${attempt} to add task: ${taskDescription}`);

      const result = await codebolt.taskplaner.addTask(taskDescription);

      if (result.success) {
        console.log(`âœ… Task added successfully on attempt ${attempt}`);
        return result;
      } else {
        lastError = result.error || 'Unknown error';
        console.warn(`âš ï¸ Attempt ${attempt} failed: ${lastError}`);
      }
    } catch (error) {
      lastError = error.message;
      console.warn(`âš ï¸ Attempt ${attempt} failed with exception: ${lastError}`);
    }

    if (attempt < maxRetries) {
      const delay = attempt * 1000; // Exponential backoff
      console.log(`â³ Waiting ${delay}ms before retry...`);
      await new Promise(resolve => setTimeout(resolve, delay));
    }
  }

  throw new Error(`Failed to add task after ${maxRetries} attempts. Last error: ${lastError}`);
};

// Usage
try {
  await addTaskWithRetry('Critical task - must be added');
} catch (error) {
  console.error('âŒ Task addition failed permanently:', error.message);
}

