const addKarmaWithLimit = async (
  agentId: string,
  amount: number,
  reason: string,
  maxKarmaPerDay: number = 50
) => {
  // Get current karma history
  const history = await codebolt.agentPortfolio.getKarmaHistory(agentId, 100);

