    { field: 'payload.statusCode', operator: 'gte', value: 400 },
    { field: 'timestamp', operator: 'gte', value: Date.now() - 3600000 }
  ],
  reduce: {
    type: 'count',
    groupBy: ['payload.statusCode']
  },
  orderBy: {
    field: 'timestamp',
    direction: 'desc'
  }
});

