        if (!validBranchName) {
            console.error("âŒ Invalid branch name. Use only letters, numbers, hyphens, underscores, and forward slashes");
            return false;
        }

        // Check if branch already exists
        const status = await codebolt.git.status();
        if (!status.success) {
            console.error("âŒ Unable to get repository status");
            return false;
        }

        // Check for uncommitted changes
        const hasUncommitted = status.data?.modified.length > 0 || 
                              status.data?.not_added.length > 0 || 
                              status.data?.staged.length > 0;

        if (hasUncommitted) {
            console.log("âš ï¸  Warning: You have uncommitted changes");
            console.log("ðŸ’¡ Consider committing changes before creating a new branch");
        }

        // Create branch
        const branchResult = await codebolt.git.branch(branchName);

        if (branchResult.success) {
            console.log(`âœ… Branch created successfully: ${branchResult.branch}`);
            console.log(`ðŸ’¡ Use 'codebolt.git.checkout("${branchName}")' to switch to it`);
