                console.error(`âŒ Branch '${branchName}' already exists`);
            } else {
                console.error("âŒ Failed to create branch:", branchResult.error);
            }
            return false;
        }
    } catch (error) {
        console.error("Error in branch validation:", error);
        return false;
    }
}

