            console.error("âŒ Cannot pull with unresolved conflicts:");
            status.data.conflicted.forEach(file => console.log(`  - ${file}`));
            return false;
        }

        // Perform pull
        const pullResult = await codebolt.git.pull();

        if (pullResult.success) {
