}

// Example 5: Push with retry logic
async function pushWithRetry(maxRetries = 3) {
    for (let attempt = 1; attempt <= maxRetries; attempt++) {
        try {
            console.log(`ðŸ”„ Push attempt ${attempt}/${maxRetries}`);

            const pushResult = await codebolt.git.push();

            if (pushResult.success) {
                console.log("âœ… Push successful");
                return true;
            } else {
                console.log(`âŒ Push attempt ${attempt} failed:`, pushResult.error);

                // If we're behind remote, try to pull and retry
                if (pushResult.error?.includes('rejected') || pushResult.error?.includes('non-fast-forward')) {
                    console.log("ðŸ”„ Attempting to pull remote changes...");

                    const pullResult = await codebolt.git.pull();
                    if (pullResult.success) {
                        console.log("âœ… Pulled remote changes");
                        // Continue to next retry attempt
                    } else {
                        console.error("âŒ Failed to pull:", pullResult.error);
                        break;
                    }
                } else {
                    // For other errors, don't retry
                    break;
                }
            }
        } catch (error) {
            console.error(`Error in push attempt ${attempt}:`, error);
        }

        // Wait before retry (except on last attempt)
        if (attempt < maxRetries) {
            console.log("â³ Waiting before retry...");
            await new Promise(resolve => setTimeout(resolve, 2000));
        }
    }

    console.error("âŒ All push attempts failed");
    return false;
}

