      instanceId,
      { description }
    );

    results.push({
      instanceId,
      success: result.success,
      error: result.error
    });
  }

  const successful = results.filter(r => r.success).length;
  console.log(`Updated ${successful}/${updates.length} instances`);

  return results;
}

