// Example 7: Bulk task creation with progress tracking
const bulkAddTasks = async (taskList, onProgress) => {
  const results = {
    successful: [],
    failed: [],
    total: taskList.length
  };

  console.log(`ðŸ”„ Adding ${taskList.length} tasks...`);

  for (let i = 0; i < taskList.length; i++) {
    const task = taskList[i];

    try {
      const result = await codebolt.taskplaner.addTask(task);

      if (result.success) {
        results.successful.push({ task, result });
        console.log(`âœ… [${i + 1}/${taskList.length}] Added: ${task}`);
      } else {
        results.failed.push({ task, error: result.error });
        console.error(`âŒ [${i + 1}/${taskList.length}] Failed: ${task}`);
      }
    } catch (error) {
      results.failed.push({ task, error: error.message });
      console.error(`âŒ [${i + 1}/${taskList.length}] Exception: ${task}`);
    }

    // Call progress callback if provided
    if (onProgress) {
      onProgress(i + 1, taskList.length, results);
    }

    // Small delay to avoid overwhelming the system
    await new Promise(resolve => setTimeout(resolve, 100));
  }

  console.log(`âœ… Bulk operation completed: ${results.successful.length} successful, ${results.failed.length} failed`);

  return results;
};

