// Example 6: Interactive command management
const interactiveCommandManager = () => {
    let currentCommand = null;
    let commandHistory = [];

    const startCommand = (command) => {
        if (currentCommand) {
            console.log('âš ï¸ Another command is already running. Stop it first.');
            return null;
        }

        console.log(`ðŸš€ Starting: ${command}`);
        currentCommand = {
            command,
            emitter: codebolt.terminal.executeCommandWithStream(command),
            startTime: Date.now()
        };

        currentCommand.emitter.on('commandOutput', (data) => {
            console.log('ðŸ“¡', data.output);
        });

        currentCommand.emitter.on('commandError', (error) => {
            console.error('âŒ', error.error);
        });

        currentCommand.emitter.on('commandFinish', (finish) => {
            const duration = Date.now() - currentCommand.startTime;

            console.log('ðŸ Command finished');
            console.log(`Duration: ${Math.round(duration / 1000)}s`);
            console.log(`Exit code: ${finish.exitCode}`);

            commandHistory.push({
                command: currentCommand.command,
                duration: Math.round(duration / 1000),
                exitCode: finish.exitCode,
                timestamp: new Date().toISOString()
            });

            currentCommand = null;
        });

        return currentCommand;
    };

    const stopCommand = async () => {
        if (!currentCommand) {
            console.log('âš ï¸ No command is currently running');
            return { success: false, message: 'No active command' };
        }

        console.log(`ðŸ›‘ Stopping: ${currentCommand.command}`);

        try {
            const interruptResult = await codebolt.terminal.sendManualInterrupt();

            if (interruptResult.success) {
                console.log('âœ… Stop signal sent successfully');
            } else {
                console.log('âŒ Failed to send stop signal');
            }

            return interruptResult;
        } catch (error) {
            console.error('âŒ Exception stopping command:', error.message);
            return { success: false, message: error.message };
        }
    };

    const getStatus = () => {
        return {
            currentCommand: currentCommand ? {
                command: currentCommand.command,
                duration: Math.round((Date.now() - currentCommand.startTime) / 1000)
            } : null,
            history: commandHistory
        };
    };

    return {
        start: startCommand,
        stop: stopCommand,
        status: getStatus
    };
};

// Usage
const manager = interactiveCommandManager();

// Start a command
manager.start('npm run dev');

// Check status
console.log('Current status:', manager.status());

// Stop the command after some time
setTimeout(async () => {
    const stopResult = await manager.stop();
    console.log('Stop result:', stopResult);
}, 5000);

// Example 7: Batch interrupt for multiple processes
const interruptMultipleProcesses = async (processNames = []) => {
    console.log('ðŸ”„ Interrupting multiple processes...');

    const results = [];

    for (let i = 0; i < processNames.length; i++) {
        const processName = processNames[i];
        console.log(`[${i + 1}/${processNames.length}] Interrupting ${processName}...`);

        try {
            const interruptResult = await codebolt.terminal.sendManualInterrupt();

            results.push({
                processName,
                success: interruptResult.success,
                message: interruptResult.message,
                timestamp: new Date().toISOString()
            });

            if (interruptResult.success) {
                console.log(`âœ… ${processName} interrupted successfully`);
            } else {
                console.log(`âŒ Failed to interrupt ${processName}: ${interruptResult.message}`);
            }

            // Small delay between interrupts
            if (i < processNames.length - 1) {
                await new Promise(resolve => setTimeout(resolve, 500));
            }
        } catch (error) {
            console.error(`âŒ Exception interrupting ${processName}:`, error.message);

            results.push({
                processName,
                success: false,
                message: error.message,
                timestamp: new Date().toISOString(),
                exception: true
            });
        }
    }

    // Summary
    const successful = results.filter(r => r.success).length;
    const failed = results.filter(r => !r.success).length;

    console.log('ðŸ“Š Interrupt Summary:');
    console.log(`   âœ… Successful: ${successful}`);
    console.log(`   âŒ Failed: ${failed}`);
    console.log(`   ðŸ“ˆ Success rate: ${Math.round(successful / results.length * 100)}%`);

    return results;
};

// Usage
const processesToStop = ['Development Server', 'API Server', 'Background Worker'];
const interruptResults = await interruptMultipleProcesses(processesToStop);

// Example 8: Smart interrupt with retry logic
const smartInterrupt = async (maxRetries = 3, retryDelay = 1000) => {
    let attempt = 1;

    while (attempt <= maxRetries) {
        console.log(`ðŸ”„ Interrupt attempt ${attempt}/${maxRetries}...`);

        try {
            const interruptResult = await codebolt.terminal.sendManualInterrupt();

            if (interruptResult.success) {
                console.log(`âœ… Interrupt successful on attempt ${attempt}`);
                return {
                    success: true,
                    attempts: attempt,
                    result: interruptResult
                };
            } else {
                console.log(`âŒ Interrupt failed on attempt ${attempt}: ${interruptResult.message}`);

                if (attempt < maxRetries) {
                    console.log(`â³ Retrying in ${retryDelay}ms...`);
                    await new Promise(resolve => setTimeout(resolve, retryDelay));
                }
            }
        } catch (error) {
            console.error(`âŒ Exception on attempt ${attempt}:`, error.message);

            if (attempt < maxRetries) {
                console.log(`â³ Retrying in ${retryDelay}ms...`);
                await new Promise(resolve => setTimeout(resolve, retryDelay));
            }
        }

        attempt++;
    }

    console.log(`âŒ All ${maxRetries} interrupt attempts failed`);
    return {
        success: false,
        attempts: maxRetries,
        message: 'All retry attempts exhausted'
    };
};

// Usage
const smartResult = await smartInterrupt(3, 2000);
console.log('Smart interrupt result:', smartResult);
